import React, { useState } from 'react';
import './App.css';

const App: React.FC = () => {
	const currencies = [ 'EUR', 'PLN', 'GEL', 'DKK', 'CZK', 'GBP', 'SEK', 'USD', 'RUB' ];
	const [ todos, setTodos] = useState(['EUR', 'PLN']);


  const addCurrency = (rate: string ) => {
    const newTodos: string[] = [ ...todos, rate];
    // console.log(newTodos);

		setTodos(newTodos);
  };


	return (
		<div>
			<div>
				{currencies.map((rate: string) => (
					<label className="rate-box" onClick={() => addCurrency(rate)}>
						<input type="checkbox" />
						{rate}
					</label>
				))}
			</div>
		</div>
	);
};

export default App;
