import React, { useState } from 'react';
import './App.css';

const App: React.FC = () => {
	const currencies = [ 'EUR', 'PLN', 'GEL', 'DKK', 'CZK', 'GBP', 'SEK', 'USD', 'RUB' ];
	const [ todos, setTodos ] = useState(['EUR']);


  const addTodo = (text: any ) => {
    const NewTodos = [ ...todos, { text } ];

		// setTodos(NewTodos);
  };


	return (
		<div>
			<div>
				{todos.map((rate) => (
					<label className="rate-box">
						<input type="checkbox" />
						{rate}
					</label>
				))}
			</div>
		</div>
	);
};

export default App;
