import React from 'react';
import logo from './logo.svg';
import './App.css';
const currencies: string[] = [ 'EUR', 'PLN', 'GEL', 'DKK', 'CZK', 'GBP', 'SEK', 'USD', 'RUB' ];
const App: React.FC<{
	state: string[];
	selectItem: (item: string) => void;
}> = ({ state, selectItem }) => {
	function isItemChecked(allItem: string[], item: string) {
		return allItem.includes(item);
	}
  return (
    <div className="listed-currencies">
			{currencies.map((item: string, index: number) => (
				<div key={index}>
					<label>
						<input
							className="checkbox"
							type="checkbox"
							checked={isItemChecked(state, item)}
							onChange={() => {
								selectItem(item);
							}}
						/>
						{item}
					</label>
				</div>
			))}
		</div>
	);

}

export default App;
