import React, { useState } from 'react';
import { Button, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.scss';

const App: React.FC = () => {
	let currencies = [ 
		{id: 0 , name: 'EUR', checked: false},
		{id: 1 , name: 'PLN', checked: false},
		{id: 2 , name: 'GEL', checked: false},
		{id: 3 , name: 'DKK', checked: false},
		{id: 4 , name: 'CZK', checked: false},
		{id: 5 , name: 'GBP', checked: false},
		{id: 6 , name: 'SEK', checked: false},
		{id: 7 , name: 'USD', checked: false},
		{id: 8 , name: 'RUB', checked: false}
	 ];

	const [ selectedCurrencies, setSelectedCurrencies ] = useState();
	// const [ check, setCheck ] = useState(false);


	const addCurrency = (rate: any) => {
		if (selectedCurrencies === undefined) {
			const addNewCurrency: object[] = [ rate ];
			console.log(rate.checked);
		rate.checked=true

			setSelectedCurrencies(addNewCurrency);


		} else {
			if (selectedCurrencies.includes(rate)) {
				removeCurrency(rate);
			} else {
				const addNewCurrency: object[] = [ ...selectedCurrencies, rate ];
				setSelectedCurrencies(addNewCurrency);


			}
		}
	};

	const removeCurrency = (rate: object) => {
		const index = selectedCurrencies.indexOf(rate);
		const removeNewCurrency = [ ...selectedCurrencies ];
		removeNewCurrency.splice(index, 1);
		setSelectedCurrencies(removeNewCurrency);


	};

	return (
		<Row className="full-screen">
			<Col xs={8} md={6}  lg={4} className="box">
				<Col className="blue">
					{console.log(selectedCurrencies === undefined)}

					{selectedCurrencies === undefined ? (
						<Row />
					) : (
						<Row>
							{selectedCurrencies.map((rate: any) => (
								<Col md={3} className="rate-box">
									<p>{}</p>

									<a className="boxclose" onClick={() => removeCurrency(rate)} />
								</Col>
							))}
						</Row>
					)}
				</Col>

				<Row className="red">
					{currencies.map((rate) => (
						<Col xs={3} key={rate.id} className="rate-box" onInput={() => addCurrency(rate)} >

<label className="container">
  <input type="checkbox" checked={rate.checked}/>
  <span>{rate.name}</span>
</label>
</Col>

					))}
				</Row>
			</Col>
		</Row>
	);
};

export default App;
