import React, { useState } from 'react';
import './App.css';

const currencies: string[] = [ 'EUR', 'PLN', 'GEL', 'DKK', 'CZK', 'GBP', 'SEK', 'USD', 'RUB' ];

const App: React.FC<{
	state: string[];
	selectItem: (item: string) => void;
}> = ({ state, selectItem }) => {
	function isItemChecked(btnItem: string[], item: string) {
		return btnItem.includes(item);
	}


return (
		<div>
			{currencies.map((item: string, index: number) => (
				<div key={index}>
					<label>
						<input
							checked={isItemChecked(state, item)}
							onChange={() => {
								selectItem(item);
							}}
						/>
						{item}
					</label>
				</div>
			))}
		</div>
	);
};

export default App;
