import React, { useState } from 'react';
import './App.css';

const currencies: string[] = [ 'EUR', 'PLN', 'GEL', 'DKK', 'CZK', 'GBP', 'SEK', 'USD', 'RUB' ];

const App: React.FC = () => {


return (
		<div>
			{currencies.map((item: string, index: number) => (
				<div key={index}>
					<label>
						<input
							checked={isItemChecked(state, item)}
							onChange={() => {
								selectItem(item);
							}}
						/>
						{item}
					</label>
				</div>
			))}
		</div>
	);
};

export default App;
