import React, { useState } from 'react';
import './App.css';


 interface currencyArray {
  selectedCurrencies: string[];
  setSelectedCurrencies: string[];
}

const App: React.FC = () => {
	const currencies = [ 'EUR', 'PLN', 'GEL', 'DKK', 'CZK', 'GBP', 'SEK', 'USD', 'RUB' ];
	const [ selectedCurrencies, setSelectedCurrencies] = useState();


  const addCurrency = (rate: string ) => {
    const newTodos: string[] = [ ...selectedCurrencies, rate];
    console.log(newTodos);

		setSelectedCurrencies(newTodos);
  };


	return (
		<div>
			<div>
				{currencies.map((rate: string) => (
					<label  key={currencies.rate} className="rate-box" onClick={() => addCurrency(rate)}>
						<input type="checkbox" />
						{rate}
					</label>
				))}
			</div>
		</div>
	);
};

export default App;
